import React,{useState} from 'react'//for state object as ith grabs value from form
import {Link} from 'react-router-dom'
import Layout from '../core/Layout'
import {signup} from '../auth/index'
const Signup=()=> {
    const [values,setValues]=useState({
        name:"",
        email:"",
        password:"",
        error:"",
        success:false
    })

    const{name,email,password,error,success}=values

const handleChange=name=>event=>{//one fuction returning another function
setValues({...values,error:false,[name]:event.target.value})
}

const clickSubmit=event=>{
    event.preventDefault()//this fuction restricts page from reloading to grab values
    setValues({...values,error:false})
    signup({name,email,password}).then(data=>{
        if(data.error){
            setValues({...values,error:data.error,success:false})
        }else{
            setValues({
                ...values,
                name:'',
                email:'',
                password:'',
                error:'',
                success:true
            })
        }
               
    })
}
    const signUpForm=()=>(
        <form>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input 
                onChange={handleChange('name')} 
                type="text" 
                className="form-control" 
                values={name}
                />
            </div>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input 
                onChange={handleChange('email')} 
                type="email" 
                className="form-control"
                values={email}
                />
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input 
                onChange={handleChange('password')} 
                type="password" 
                className="form-control"
                values={password}
                />
            </div>
            <button onClick={clickSubmit} className="btn btn-primary">Submit</button>
        </form>
    )

const showError=()=>(
    <div className="alert alert-danger" style={{display:error?'':'none'}}>
        {error}
    </div>
)
const showSucess=()=>(
    <div className="alert alert-info" style={{display:success?'':'none'}}>
        New account is created.Please <Link to='/signin'>Signin!!</Link>
    </div>
)

    return(
    <Layout 
    title="Signup" 
    description="Signup to Node React E-commerce App"
    className="container col-md-8 offset-md-2"
    >
        {showSucess()}
        {showError()}
    {signUpForm()}

    </Layout>
    )
}

export default Signup;
